import React, { useState, useEffect } from "react";
import Navbar from "./components/Navbar";
import Filters, { DropdownOptions } from "./components/Filters";
import RecipeList from "./components/RecipeList";
import AddRecipeModal from "./components/AddRecipeModal";
import { searchRecipes } from "./services/recipeService";

const App = () => {
    const [filters, setFilters] = useState({
        type: "",
        cuisine: "",
    });

    const [searchQuery, setSearchQuery] = useState("");
    const [recipes, setRecipes] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);

    const { types, cuisines } = DropdownOptions;

    // Load recipes from localStorage on initial render
    useEffect(() => {
        try {
            const storedRecipes = localStorage.getItem("recipes");
            setRecipes(storedRecipes ? JSON.parse(storedRecipes) : []);
        } catch (error) {
            console.error("Error loading recipes from localStorage:", error);
        }
    }, []);

    // Save recipes to localStorage whenever recipes state changes
    useEffect(() => {
        try {
            localStorage.setItem("recipes", JSON.stringify(recipes));
        } catch (error) {
            console.error("Error saving recipes to localStorage:", error);
        }
    }, [recipes]);

    const fetchRecipes = async () => {
        setLoading(true);
        setError(null);
        try {
            const filteredRecipes = await searchRecipes(searchQuery, filters);
            setRecipes(filteredRecipes);
        } catch (err) {
            setError("Failed to fetch recipes.");
        }
        setLoading(false);
    };

    useEffect(() => {
        fetchRecipes(); // Fetch recipes when filters or search query changes
    }, [filters, searchQuery]);

    const addRecipe = async (recipe) => {
        try {
            const response = await fetch("http://localhost:3001/recipes", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(recipe),
            });

            if (!response.ok) {
                throw new Error(`Failed to add recipe: ${response.statusText}`);
            }

            const newRecipe = await response.json();
            setRecipes((prevRecipes) => [...prevRecipes, newRecipe]);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <div>
            <Navbar onSearch={setSearchQuery} />
            <div className="spaced">
                <Filters filters={filters} setFilters={setFilters} />
            </div>
            <div style={{ padding: "20px" }}>
                <button onClick={() => setIsModalOpen(true)}>Add Recipe</button>
                <AddRecipeModal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)} onAddRecipe={addRecipe} types={types} cuisines={cuisines} />
            </div>
            {loading && <p>Loading...</p>}
            {!loading && error && <p>{error}</p>}
            {!loading && !error && recipes.length === 0 && <p>No recipes found. Try adjusting your search or filters.</p>}
            {!loading && !error && recipes.length > 0 && <RecipeList recipes={recipes} />}
        </div>
    );
};

export default App;
